import React, { useState } from "react";

import { BirthdayForm } from "./BirthdayForm";

export const NewBirthday = (props) => {
  const [isEditing, setIsEditing] = useState(false);
  const saveBirthdayDataHandler = (enteredBirthdayData) => {
    const birthdayData = {
      ...enteredBirthdayData,
      id: Math.random().toString(),
    };
    props.onAddBirthday(birthdayData);
    setIsEditing(false);

  };
  const startEditingHandler = () => {
    setIsEditing(true);
  };
  const stopEditingHandler = () => {
    setIsEditing(false);
  };
  return (
    <div className="new-expense">
      {!isEditing && (
        <button onClick={startEditingHandler}>Add New Birhday</button>
      )}
      {isEditing && (
        <BirthdayForm
          onSaveExpenseData={saveBirthdayDataHandler}
          onCancel={stopEditingHandler}
        />
      )}
    </div>
  );
};
