const List = ({ people }) => {
  return (
    <>
      {people.map((person) => {
        const { id, name, age, image, birthday } = person;
        let today = new Date();
        let birthdate = new Date(
          birthday.substring(4, 8),
          birthday.substring(0, 2) - 1,
          birthday.substring(2, 4)
        );

        if (today.getDate() === birthdate.getDate() && today.getMonth() === birthdate.getMonth()) {
          return (
            <article key={id} className='person'>
              <img src={image} alt={name} />
              <div>
                <h4>{name}</h4>
                <p>{age} years</p>
              </div>
            </article>
          );
        }
        return null;
      })}




    </>
  );
};

export default List;
