import React from 'react';

const List = ({ people }) => {
  return (
    <>
      {people.map((person) => {
        const { id, name, age, image, birthday, birthMonth } = person;
        let date = new Date();


        let day = date.getDate();
        {/* console.log(day); // 24 */ }

        let month = date.getMonth();
        {/* console.log(month + 1); // 1 */ }

        let year = date.getFullYear();
        {/* console.log(year); // 2023 */ }
        if (day === birthday && month === birthMonth) {
          console.log("match")
        }



        return (
          <article key={id} className='person'>
            <img src={image} alt={name} />
            <div>
              <h4>{name}</h4>
              <p>{age} years</p>
            </div>
          </article>
        );
      })}
    </>
  );
};

export default List;
