import React, { useState } from "react";

export const BirthdayForm = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [entredMonth, setEnteredMonth] = useState("");
  const [entredDay, setEnteredDay] = useState("");
  const [entredYear, setEnteredYear] = useState("");

  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const monthChangeHandler = (event) => {
    setEnteredMonth(event.target.value);
  };
  const dayChangeHandler = (event) => {
    setEnteredDay(event.target.value);
  };
  const yearChangeHandler = (event) => {
    setEnteredYear(event.target.value);
  };
  const submitHandler = (event) => {
    event.preventDefault();

    const birthdayData = {
      name: enteredName,
      month: entredMonth,
      day: entredDay,
      year: entredYear,
    };
    props.onSaveExpenseData(birthdayData);
    setEnteredName("");
    setEnteredMonth("");
    setEnteredDay("");
    setEnteredYear("");
  };
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__controls">
          <label>Name</label>
          <input type="text" value={enteredName} onChange={nameChangeHandler} />
        </div>
        <div className="new-expense__controls">
          <label>Month</label>
          <input
            type="number"
            min="1"
            max="12"
            step="1"
            value={entredMonth}
            onChange={monthChangeHandler}
          />
        </div>
        <div className="new-expense__controls">
          <label>Day</label>
          <input
            type="number"
            min="1"
            max="31"
            step="1"
            value={entredDay}
            onChange={dayChangeHandler}
          />
        </div>
        <div className="new-expense__controls">
          <label>Year</label>
          <input
            type="number"
            maxLength={4}
            value={entredYear}
            onChange={yearChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button type="Submit">Add Birthday</button>
      </div>
    </form>
  );
};
