const List = ({ people }) => {
  return (
    <>
      {people.map((person) => {
        const { id, name, age, image } = person;
        let date = new Date();
        let birthday = new Date(person.birthday);
        if (date.getMonth() === birthday.getMonth() && date.getDate() === birthday.getDate()) {
          return (
            <article key={id} className='person'>
              <img src={image} alt={name} />
              <div>
                <h4>{name}</h4>
                <p>{age} years</p>
              </div>
            </article>
          );
        }
      })}

    </>
  );
};

export default List;
